import React from 'react';
import logo from './logo.svg';
import Employees from './components/Employees/Employees';
import './App.css';
import Navbar from './components/Navbar/Navbar';
import Buttons from './components/Buttons/Buttons';
import Search from './components/Search/Search';


function App() {

  state = {
    users: [],
    input: '',
    filteredUsers: []
  }
  componentDidMount =()=>{
    getUsers()
    .then(result => {
      this.setState({users: result.data.results, filteredUsers: result.data.results})
    })
  }
  handleChange =(e)=>{
    const filtered = this.state.users.filter(user=>user.name.title.toLowerCase().includes(e.target.value))
    this.setState({input:e.target.value, filteredUsers:filtered})
  }


  return (
    <div className="App">
      <Navbar />
      <Search />
      <Buttons />
      <Employees />
    </div>
  );
}

export default App;
