import React, { Component } from 'react';

export default class Search extends Component () {

    state = {
        users: [],
        input: '',
        filteredUsers: []
      }
      componentDidMount =()=>{
        getUsers()
        .then(result => {
          this.setState({users: result.data.results, filteredUsers: result.data.results})
        })
      }
      handleChange =(e)=>{
        const filtered = this.state.users.filter(user=>user.name.title.toLowerCase().includes(e.target.value))
        this.setState({input:e.target.value, filteredUsers:filtered})
      }

      render() {
    const style = {
        margin: '10px',
         justifyContent: 'center'
    }

    // let filteredEmployees = this.props.employees.filter(
    //     (employee) => {
    //         return employee.name.indexOf(this.state.search) !== -1;
    //     }
    // )

    return (
        <form style={style} class="form-inline">
            <input class="form-control mr-sm-2 col-md-4" type="search" placeholder="Search by Employee Name" aria-label="Search" onChange={this.handleChange}/>
            <button class="btn btn-success my-2 my-sm-0" type="submit">Search</button>
        </form>

    );
    }
}
